const template = document.getElementById("card-template");
const contenedor = document.getElementById("contenedor");

//Este es un arreglo de objetos, cada objeto posee el titulo y descripcion 
//que añadiremos en las copias de esta plantilla
const productos = [
    { titulo: "Laptop", desc: "16GB RAM", price: "1.000", image: "https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcSWDmoL9xp1bAky1u_-0mUlvAjHl-h6uKpN_Cpjd9wCp1AS1y4bFPADXKp-GDKMbk7IWvJzFraugghmY7mZHTEHODqboPgLyJnj-44573Kcvu582lnW7x6Rmg" },
    { titulo: "Mouse", desc: "Inalámbrico", price: "50", image: "https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcSNVUdiunBARdpX0MQ_WY9HQPirLmulV0xQdtoLjVc424aIJWWQfMrAia30i7v7CJyWDIZsdgakZ7jm7DkO7gkcqjlu33v1mup5bb8-YShe_v7BuaRVaN2FrM3PzhcSEmwMmi_j8NSZL8s&usqp=CAc" },
    { titulo: "Teclado", desc: "Mecánico, RGB", price: "90", image: "data:image/jpeg;base64," },
    { titulo: "Monitor", desc: "27 pulgadas, 144Hz", price: "300", image: "data:image/jpeg;base64," },
    { titulo: "Auriculares", desc: "Con cancelación de ruido", price: "120", image: "data:image/jpeg;base64," },
    { titulo: "Webcam", desc: "Full HD 1080p", price: "65", image: "data:image/jpeg;base64," },
    { titulo: "Disco duro externo", desc: "2TB USB 3.0", price: "80", image: "data:image/jpeg;base64,"}
];
//En un forEach iteraremos cada elemento
productos.forEach(producto => {
    const clon = template.content.cloneNode(true); //Clonamos la plantilla

    //Añadimos el titulo a este clon, su valor sera respecto al valor de el profucto en esta iteración
    clon.querySelector(".title").textContent = producto.titulo;

    //Exactamente lo mismo, pero con la descripción
    clon.querySelector(".desc").textContent = producto.desc;

    clon.querySelector(".price").textContent = producto.price;

    clon.querySelector(".img").setAttribute("src", producto.image);

    //clon.setAttribute("class","product-card");

    //Ahora, añadimos este clon en un contenedor, en este caso es un div
    contenedor.appendChild(clon);
});
